function toggleFieldsVisibility(frm) {
    if (frappe.user_roles.includes("MR Creator")) {
        frm.fields_dict['items'].get_query = function() {
            return {
                filters: [
                    // Set in_list_view to 0
                    ['Stock Entry Detail', 'in_list_view', '=', 0]
                ]
            };
        };
        frm.fields_dict['items'].grid.toggle_display('basic_rate', false);
        frm.fields_dict['items'].grid.toggle_display('basic_amount', false);
        frm.fields_dict['items'].grid.toggle_display('valuation_rate', false);
        frm.fields_dict['items'].grid.toggle_display('amount', false);
    } else if (frappe.user_roles.includes("Stock User") || frappe.user_roles.includes("Stock Manager") || frappe.session.user == "Administrator"){
        frm.fields_dict['items'].grid.toggle_display('basic_rate', true);
        frm.fields_dict['items'].grid.toggle_display('basic_amount', true);
        frm.fields_dict['items'].grid.toggle_display('valuation_rate', true);
        frm.fields_dict['items'].grid.toggle_display('amount', true);
    }
}




