frappe.ui.form.on('Delivery Note', {
  packing_scanner: function (frm) {
    var packing = cur_frm.doc.packing_scanner;
    console.log(packing);

    frappe.call({
      method: "packing",
      args: {
        "packing_list": packing
      },
      callback: function (records) {
        $.each(frm.doc.items || [], function (i, d) {
          var sales_order_id = d.against_sales_order;
          var item_code = records.item_code.split(",");
          var serial_no = records.serial_no.split(",");
          var item_code1 = records.item_code1.split(",")
          var serial_count = records.serial_count.split(",")
          var as = records.sales_length.split(",")
          var item_code2 = records.item_code2.split(",")
          var item_code3 = records.item_code3.split(",")
          var weight = records.sales_weight.split(",")
          var item_code4 = records.item_code4.split(",")
          var grade = records.sales_grade.split(",")
          var item_code5 = records.item_code5.split(",")
          var item_container = records.container.split(",")


          var index = item_code.indexOf(d.item_code);
          var index_z = item_code1.indexOf(d.item_code);
          var index_length = item_code2.indexOf(d.item_code)
          var index_weight = item_code3.indexOf(d.item_code)
          var index_grade = item_code4.indexOf(d.item_code)
          var index_container = item_code5.indexOf(d.item_code)
          if (index !== -1 && d.against_sales_order === sales_order_id && index_z !== -1 && index_length !== -1 && index_weight !== -1  && index_grade !== -1 && index_container !== -1) {
            d.serial_no = serial_no[index] ;
            d.qty = serial_count[index_z]
            d.packing_length = as[index_length]
            d.packing_weight = weight[index_weight]
            d.packing_grade = grade[index_grade]
            d.container_no = item_container[index_container]
            console.log("yes");
          } else {
            console.log("no");
          }
        });
      }
    });
  }
});


