frappe.ui.form.on('Retention Bonus', {
    get_salary_slip(frm) {
        var monthly_bonus = cur_frm.doc.monthly_bonus;
        var emp = cur_frm.doc.employee;
        var from_date = cur_frm.doc.from_date;
        var to_date = cur_frm.doc.to_date;

        if (monthly_bonus === 1 && emp && from_date && to_date) {
            frappe.call({
                method: "advantisquartz.advantisquartz.doctype.api.get_employee_bonus",
                args: {
                    employee: emp,
                    from_date: from_date,
                    to_date: to_date
                },
                async: false,
                callback: function (response) {
                    console.log(response.message);

                    frm.clear_table("employee_bonus");

                    var salary_slips = response.message[0];
                    var get_salary_data = response.message[1];
                    var totalBonus = 0;

                    for (var i = 0; i < salary_slips.length; i++) {
                        var row = frm.add_child("employee_bonus");
                        row.salary_slip = salary_slips[i].name;
                        row.basic_salary = get_salary_data[i].amount;

                        var amount = get_salary_data[i].amount;
                        if (amount <= 7000) {
                            row.bonus = amount * 0.0833; 
                        } else if (amount <= 21000) {
                            row.bonus = 7000 * 0.20; 
                        } else {
                            row.bonus = 0;
                        }
                        totalBonus += row.bonus;
                    }

                    frm.refresh_field("employee_bonus");
                    frm.set_value("salary_component", "Bonus");
                    totalBonus = parseFloat(totalBonus.toFixed(0));
                    frm.set_value("bonus_amount", totalBonus);
                }
            });
        }
    }
});

