script:
frappe.ui.form.on('Gate Entry', {
	is_weight_in: function(frm) {
		frm.set_value('weight_in_date', frappe.datetime.now_date())
		frm.set_value('weight_in_time',frappe.datetime.now_time())
	},
	is_unloaded: function(frm){
	    frm.set_value('unloading_date', frappe.datetime.now_date() )
	    frm.set_value('unloading_time', frappe.datetime.now_time())
	},

	is_weight_out: function(frm)
	{
        frm.set_value('weight_out_date', frappe.datetime.now_date() )
        frm.set_value('weight_out_time', frappe.datetime.now_time())
	},
	is_gate_out: function(frm)
	{
	    frm.set_value('gate_out_date', frappe.datetime.now_date() )
        frm.set_value('gate_out_time', frappe.datetime.now_time())
	}
})

frappe.ui.form.on('Purchase Order', {

    onload :function(frm)
    {
        var a = cur_frm.doc.transaction_date;

        frm.set_value('schedule_date',frappe.datetime.add_days(a,7) );
        console.log(a);
    }
});


frappe.ui.form.on('Purchase Order', {
	before_submit: function(frm) {
		var a = [];
		frm.doc.items.forEach(function(child) {
			if (child.supplier_quotation) {
				a.push(child.supplier_quotation);
			}
		});
		a = [...new Set(a)];

		if (a.length > 0) {
			var output = a.join(', '); 

		}
		frappe.db.set_value('Supplier Quotation',output, 'supplier_quotation_status',"Received")
	}
});

frappe.ui.form.on("Quotation Item", "qty",function(frm, cdt, cdn){
    var d = locals[cdt][cdn];
    frappe.model.set_value(cdt, cdn, "sales_qty_as_per_sales__uom", d.qty * d.sales_uom_conversion_factor);

    cur_frm.refresh_field("Quotation Item");
});


frappe.ui.form.on("Quotation Item", "sales_uom_conversion_factor",function(frm, cdt, cdn){
    var d = locals[cdt][cdn];
    frappe.model.set_value(cdt, cdn, "sales_qty_as_per_sales__uom", d.qty * d.sales_uom_conversion_factor);
    cur_frm.refresh_field("Quotation Item");
});

frappe.ui.form.on('Sales Order Item', {
	sales_uom_conversion_factor:function(frm) {
	 $.each(frm.doc.items || [], function (i, d)
	 {
	     var sales_uom_cf = d.sales_uom_conversion_factor;
	     var sales_uom_multipy = sales_uom_cf * d.qty;

	 })
	}
})

frappe.ui.form.on('Stock Entry Detail', {
	item_code: function(frm) {
		var currentSeries = cur_frm.doc.manual_serial_series;
		var qty = 0;
	    var year = new Date().getFullYear();
	    console.log(year)
         $.each(frm.doc.items, function(i, d) {
            qty += d.qty;
        });

     if (currentSeries.split("#").length - 1 === 1) {
			for (var i = 0; i < qty; i++) {
				var paddedIndex = i.toString().padStart(2, '0'); // Add leading zeros if the index is a single digit
				console.log(currentSeries.replace("#", paddedIndex));
			}
		} else if (currentSeries.split("#").length - 1 === 2) {

			for (var j = 0; j < qty ; j++){
			    var paddedjindex = j.toString().padStart(2,'0');
			    console.log(currentSeries.replace("##",paddedjindex))
			}
		}
		else if (currentSeries.split("#").length - 1 === 3 || currentSeries.includes("YY")) {
			for (var k = 0; k < qty; k++) {
				var paddedkindex = k.toString().padStart(3, '00');
				console.log(currentSeries.replace(/YY/g, year).replace("###", paddedkindex));
			}
		}
		}

});

frappe.ui.form.on('Request for Quotation', {
  validate: function(frm) {
    var a = [];
    frm.doc.suppliers.forEach(function(child) {
      if (child.quote_status) {
        a.push(child.quote_status);
      }
    });

    if (a.includes("Received")) {
      frm.set_value("quotation_status","Received")
    } else {
      frm.set_value("quotation_status","Pending")
    }
  }
});

frappe.ui.form.on('Supplier Quotation', {
	before_submit: function(frm) {
		var a = [];
		frm.doc.items.forEach(function(child) {
			if (child.request_for_quotation) {
				a.push(child.request_for_quotation);
			}
		});
		a = [...new Set(a)];

		if (a.length > 0) {
			var output = a.join(', '); 

		}
		frappe.db.set_value('Request for Quotation',output, 'quotation_status',"Received")
	}
});
frappe.ui.form.on('Supplier Quotation', {
  validate: function(frm) {

      frm.set_value("supplier_quotation_status","Pending")

  }
});
