frappe.ui.form.on('Stock Entry', {
  refresh: function(frm) {
    frm.set_query('product_type', function() {
      return {
        options: 'Semi Finished Item\nFinished Item'
      };
    });

    // Triggered when the form is loaded
    frm.add_custom_button(__('Update Serial No'), function() {
      // Function to open the dialog box
      showDialog(frm);
    });
  }
});

function showDialog(frm) {
  // Create the dialog box
  let d = new frappe.ui.Dialog({
    title: __('Serial No'),
    fields: [
      {
        fieldtype: 'Select',
        fieldname: 'product_type',
        label: __('Product Type'),
        options: 'Semi Finished Item\nFinished Item',
        onchange: function() {
          // Refresh the dialog to show/hide the table based on the selected value
          d.refresh();
        }
      },
      {
        fieldname: 'items',
        fieldtype: 'Table',
        label: __('Serial No'),
        depends_on: 'eval: doc.product_type == "Semi Finished Item"',
        fields: [
          {
            fieldtype: 'Link',
            options: 'Serial No',
            fieldname: 'serial_no',
            label: __('Serial No'),
            in_list_view: 1
          },
          {
            fieldtype: 'Data',
            fieldname: 'batch_no',
            label: __('Batch'),
            in_list_view: 1
          },
          {
            fieldtype: 'Select',
            fieldname: 'serial_auto',
            label: __('Serial Type'),
            in_list_view: 1,
            options: 'Auto\nManual'
          }
        ]
      },
       {
        fieldname: 'finished_items',
        fieldtype: 'Table',
        label: __('Finished Item'),
        depends_on: 'eval: doc.product_type == "Finished Item"',
        fields: [
          {
            fieldtype: 'Link',
            options: 'Item',
            fieldname: 'item_code',
            label: __('Item Code'),
            in_list_view: 1
          },
          {
            fieldtype: 'Data',
            fieldname: 'Item Name',
            label: __('Item Name'),
            in_list_view: 1,
          },
          {
            fieldtype: 'Select',
            fieldname: 'serial_auto',
            label: __('Serial Type'),
            in_list_view: 1,
            options: 'Auto\nManual'
          }
        ]
      }
    ],
    primary_action: function() {
      let values = d.get_values();
      let items = values.items;
      for (let i = 0; i < items.length; i++) {
        let item = items[i];
        frappe.msgprint('Serial Type: ' + item.serial_auto);
         frappe.db.set_value('Serial No',item.serial_no, {
                'serial_no_type': item.serial_auto,
                'batch_no':item.batch_no
            });
      }

      d.hide();
    },
    primary_action_label: __('Add')
  });

  d.show();
}

frappe.ui.form.on('Gate Entry', {
	is_weight_in: function(frm) {
		frm.set_value('weight_in_date', frappe.datetime.now_date());
		frm.set_value('weight_in_time',frappe.datetime.now_time());
	},
	is_unloaded: function(frm){
	    frm.set_value('unloading_date', frappe.datetime.now_date());
	    frm.set_value('unloading_time', frappe.datetime.now_time());
	},

	is_weight_out: function(frm)
	{
        frm.set_value('weight_out_date', frappe.datetime.now_date());
        frm.set_value('weight_out_time', frappe.datetime.now_time());
	},
	is_gate_out: function(frm)
	{
	    frm.set_value('gate_out_date', frappe.datetime.now_date());
        frm.set_value('gate_out_time', frappe.datetime.now_time());
	},
	weight_out:function(frm){
	    var gross_weight= frm.doc.weight_in;
	    var tare_weight = frm.doc.weight_out;
	    console.log(gross_weight + "\n\n" + tare_weight);
	    frm.doc.net_weight = gross_weight-tare_weight;
	    frm.refresh_field('net_weight');
	},
	before_submit(frm){
	    var asset_repair = frm.doc.asset_repair_entry;
	    if(asset_repair){
	        frappe.db.set_value("Asset Repair",asset_repair,'gate_out_entry',frm.doc.name);
	    }
	},
	before_cancel(frm){
	    if(frm.doc.purpose == "Raw Material"){
	        frm.clear_table('qc_details');
	    }
	    var asset_repair = frm.doc.asset_repair_entry;
	    if(asset_repair){
	        frappe.db.set_value("Asset Repair",asset_repair,'gate_out_entry',' ');
	    }
	}
});

