frappe.ui.form.on('Request for Quotation', {
  validate: function(frm) {
    var a = [];
    frm.doc.suppliers.forEach(function(child) {
      if (child.quote_status) {
        a.push(child.quote_status);
      }
    });

    if (a.includes("Received")) {
      frm.set_value("quotation_status","Received")
    } else {
      frm.set_value("quotation_status","Pending")
    }
  }
});

