frappe.ui.form.on('Serial No Update', {
    refresh(frm) {
        frm.fields_dict["press_item"].grid.add_custom_button(__('Download'), function() {
            // Fetch child table data
            const childTableData = frm.doc.press_item;

            // Define a mapping of custom field names to child table field names
            const fieldMapping = {

                "serial_no": "serial_no",
                "batch_no":"batch_no",
                "select_type":"select_type",
                "width":"width",
                "length":"length",
                "weight":"weight"

                // Map "qty" to "lot_no" field in the child table
            };

            // Create a CSV string with custom field names as the first row
            const csvContent = "data:text/csv;charset=utf-8," 
                + Object.keys(fieldMapping).join(',') + '\n'
                + childTableData.map(row => Object.keys(fieldMapping).map(customField => row[fieldMapping[customField]] || "").join(',')).join('\n');

            // Create a temporary anchor element to trigger the download
            const anchor = document.createElement('a');
            anchor.href = encodeURI(csvContent);
            anchor.target = '_blank';
            anchor.download = 'child_table_data.csv';
            anchor.click();
        });

        // Change button style to match the primary color
        frm.fields_dict["press_item"].grid.grid_buttons.find('.btn-custom').removeClass('btn-default').addClass('btn-primary');
    }
});

frappe.ui.form.on('Serial No Update', {
    refresh(frm) {
        frm.fields_dict["press_item"].grid.add_custom_button(__('Upload'), function() {
            // Create a file input element dynamically
            var fileInput = document.createElement('input');
            fileInput.type = 'file';
            fileInput.accept = '.csv,.xlsx';

            // Trigger the file selection when the file input changes
            fileInput.addEventListener('change', function(event) {
                handleFileUpload(event.target.files[0]);
            });

            // Trigger the file input click event
            fileInput.click();
        });

        // Change button style to match the primary color
        frm.fields_dict["press_item"].grid.grid_buttons.find('.btn-custom').removeClass('btn-default').addClass('btn-primary');
    }  
});
function handleFileUpload(file) {
    if (!file) {
        frappe.msgprint(__('No file selected.'));
        return;
    }

    var reader = new FileReader();
    reader.onload = function(event) {
        var csvData = event.target.result;
        processData(csvData);
    };
    reader.readAsText(file);
}
function processData(csvData) {
    var lines = csvData.split('\n');
    var fieldNames = lines[0].split(','); // Assuming the first row contains field names

    // Remove the header line from the lines array
    lines.splice(0, 1);

    var pressItems = [];
    for (var i = 0; i < lines.length; i++) {
        var values = lines[i].split(',');

        // Skip empty lines
        if (values.length === 1 && values[0].trim() === '') {
            continue;
        }

        var pressItem = {};
        for (var j = 0; j < fieldNames.length; j++) {
            // Trim any leading/trailing spaces from field names and values
            var fieldName = fieldNames[j].trim();
            var value = values[j].trim();

            // Map the field name with its corresponding value
            pressItem[fieldName] = value;
        }

        pressItems.push(pressItem);
    }

    // Get the current form instance
    var frm = cur_frm;

    // Clear existing child table rows
    frm.clear_table('press_item');

    // Add new child table rows based on the CSV data
    for (var k = 0; k < pressItems.length; k++) {
        var child = frm.add_child('press_item', pressItems[k]);
    }

    // Refresh the child table to display the newly added rows
    frm.refresh_field('press_item');

    frappe.msgprint(__('CSV data has been successfully loaded into the child table.'));
}

function checkLock(frm) {
  const allFields = ['naming_series', 'entry_type', 'purpose', 'invoice_no', 'invoice_date', 'stock_item_tab', 'driver_name', 'driver_mobile_no', 'truck_no'];

  const isLocked = frm.doc.lock_fields;
  for (const field of allFields) {
    frm.set_df_property(field, 'read_only', isLocked);
  }

  if (frm.doc.entry_type === 'Inward') {
    frm.set_df_property('purpose', 'reqd', !isLocked);
    frm.set_df_property('driver_name', 'reqd', !isLocked);
    frm.set_df_property('driver_mobile_no', 'reqd', !isLocked);
    frm.set_df_property('truck_no', 'reqd', !isLocked);
    frm.set_df_property('invoice_no', 'reqd', !isLocked);
    frm.set_df_property('invoice_date', 'reqd', !isLocked);
  } else if (frm.doc.entry_type === 'Outward') {
    frm.set_df_property('purpose', 'reqd', !isLocked);
    frm.set_df_property('driver_name', 'reqd', !isLocked);
    frm.set_df_property('driver_mobile_no', 'reqd', !isLocked);
    frm.set_df_property('truck_no', 'reqd', !isLocked);
    frm.set_df_property('invoice_no', 'reqd', false);
    frm.set_df_property('invoice_date', 'reqd', false);
  } else {
    frm.set_df_property('entry_type', 'reqd', !isLocked);
  }
}

function toggleWeightLock(frm) {
  const isWeightInChecked = frm.doc.is_weight_in;
  const isLockWeightChecked = frm.doc.lock_weight;

  if (isWeightInChecked && isLockWeightChecked) {
    frm.set_df_property('uom', 'read_only', true);
    frm.set_df_property('weight_in', 'read_only', true);
     frm.set_df_property('is_weight_in', 'read_only', true);
  } else if (!isWeightInChecked && isLockWeightChecked) {
    frm.set_df_property('uom', 'read_only', true);
    frm.set_df_property('weight_in', 'read_only', false);
     frm.set_df_property('is_weight_in', 'read_only', true);
  } else {
    frm.set_df_property('uom', 'read_only', false);
    frm.set_df_property('weight_in', 'read_only', isLockWeightChecked);
    frm.set_df_property('is_weight_in', 'read_only', isLockWeightChecked);
  }
}

function toggleSamplingLock(frm) {
