function showDialog(frm) {
  // Create the dialog box
  let d = new frappe.ui.Dialog({
    title: __('Serial No'),
    fields: [
      {
        fieldtype: 'Select',
        fieldname: 'product_type',
        label: __('Product Type'),
        options: 'Semi Finished Item\nFinished Item',
        onchange: function() {
          // Refresh the dialog to show/hide the table based on the selected value
          d.refresh();
        }
      },
      {
        fieldname: 'items',
        fieldtype: 'Table',
        label: __('Serial No'),
        depends_on: 'eval: doc.product_type == "Semi Finished Item"',
        fields: [
          {
            fieldtype: 'Link',
            options: 'Serial No',
            fieldname: 'serial_no',
            label: __('Serial No'),
            in_list_view: 1
          },
          {
            fieldtype: 'Data',
            fieldname: 'batch_no',
            label: __('Batch'),
            in_list_view: 1
          },
          {
            fieldtype: 'Select',
            fieldname: 'serial_auto',
            label: __('Serial Type'),
            in_list_view: 1,
            options: 'Auto\nManual'
          }
        ]
      },
       {
        fieldname: 'finished_items',
        fieldtype: 'Table',
        label: __('Finished Item'),
        depends_on: 'eval: doc.product_type == "Finished Item"',
        fields: [
          {
            fieldtype: 'Link',
            options: 'Item',
            fieldname: 'item_code',
            label: __('Item Code'),
            in_list_view: 1
          },
          {
            fieldtype: 'Data',
            fieldname: 'Item Name',
            label: __('Item Name'),
            in_list_view: 1,
          },
          {
            fieldtype: 'Select',
            fieldname: 'serial_auto',
            label: __('Serial Type'),
            in_list_view: 1,
            options: 'Auto\nManual'
          }
        ]
      }
    ],
    primary_action: function() {
      let values = d.get_values();
      let items = values.items;
      for (let i = 0; i < items.length; i++) {
        let item = items[i];
        frappe.msgprint('Serial Type: ' + item.serial_auto);
         frappe.db.set_value('Serial No',item.serial_no, {
                'serial_no_type': item.serial_auto,
                'batch_no':item.batch_no
            });
      }

      d.hide();
    },
    primary_action_label: __('Add')
  });

  d.show();
}

