.print-format td, .print-format th {
    vertical-align: middle !important;
    padding: 3px !important;
}
.print-format {
    background-color: white;
    border-radius: 8px;
    max-width: 8.3in;
    min-height: 11.69in;
    padding: 0.50in;
    margin: auto;
    color: var(--gray-900);
}
.flex-container {
    display: flex;
    justify-content: space-between;
}
.print-format .row:not(.section-break) {
    line-height:1;
    margin-top: 0px !important;
}
